import com.eviware.soapui.support.XmlHolder
import com.eviware.*
import com.eviware.soapui.model.testsuite.TestRunner.Status
import jxl.*
filepath = "C:/Manasa/API Testing Training/Project/Test Data Rest.xls"
File Excel = new File(filepath)
//excel contain workbooks and workbok contains sheets
Workbook wb = Workbook.getWorkbook(Excel)
//trying to fetch the sheet
Sheet sh = wb.getSheet("Sheet1")
//log.info sh.getCell(0,0).getContents()
rowSize = sh.getRows().toInteger();
columnSize = sh.getColumns().toInteger();
log.info "The total number of rows which contains data are" +rowSize
log.info "The total number of columns which contains data are" +columnSize
//for loop
for(def i=0;i<rowSize;i++)
{
    for(def j=0;j<columnSize;j++)
    {
    	if(j==0)
    	{
        keyword = sh.getCell(j,i).getContents()
       }

      else 
       {
       	 text = sh.getCell(j,i).getContents()
       }

    }  
}
def tc = testRunner.testCase.testSuite.project.testSuites["REST_API_TESTCASES"].testCases["Property Expansion_Transfer_System Test_Assertion"]
//to update my property file with username and password
tc.testSteps["keyword"].setPropertyValue("keyword",keyword)
tc.testSteps["keyword"].setPropertyValue("text",text)
//tc.testSteps["Customer_Details"].setPropertyValue("lastname",lastname)
//run synchrouously
