con:config.script:
import jxl.*;
import jxl.write.*
import groovy.json.JsonSlurper

import com.eviware.*

def response = context.testCase.getTestStepByName("Get_All_Products").getPropertyValue("response")

//log.info response


def holder = new JsonSlurper().parseText(response)

def prod = holder.data
for(def p=0;p<prod.size();p++)
{
	if(prod[p].quantity<=10)
	{
		def idtoupdate = prod[p].id.toString();
		def qtytoupdate = prod[p].quantity.toString();
		context.testCase.setPropertyValue ("idtoupdate",idtoupdate)
		context.testCase.setPropertyValue ("Qty",qtytoupdate)

		testRunner.gotoStepByName ("Update_Product_ByQuantity")

		log.info "Update Successfully"	

	}
	else
	{
		log.info "Update unsuccessful"
	}

	}

